import React from "react";

const EmailForm = () => {
  const [message, setMessage] = React.useState("");
  const [input, setInput] = React.useState("");

/*   const inputChangeHandler = (event) => {
    setInput(event.target.value);
  }; */

  const emailValidation = () => {
    const emailRegex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    return !(!input || emailRegex.test(input) === false);
  };

  const countryValidation = () => {
    const countryRegex = /.co$/;
    return !input || countryRegex.test(input) === false;
  };

  const onSubmit = () => {
    const isEmailValid = emailValidation();
    const countryValid = countryValidation();

    if (Object.keys(input).length === 0) {
      setMessage("Email adress is required!");
    } else if (!countryValid) {
      setMessage(
        "We are not accepting subscriptions from Colombia emails!"
      );
    } else if (!isEmailValid) {
      setMessage(
        "Email Address not valid!"
      );
    } else {
      setMessage("Thanks for subscribing!");
    }
  };

  return (
    <div className="child-component">
      <div className="form-group mb-3">
        <label>
          <strong>Email</strong>
        </label>
        <input
          type="email"
          name="email"
          value={input}
          onChange={inputChangeHandler}
          className="form-control"
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary" onClick={onSubmit}>
          Submit
        </button>
      </div>
      <br />
      {message}
    </div>
  );
};

export default EmailForm;
