import React, { Component } from "react";

import Navigation from "./Navigation";

import Content from "./Content";

import "./App.scss";

import FormEmailComponent from "./form-email.component";



const LINKS = [
  { label: "About", to: "http://localhost:3000/" },
  { label: "How it works", to: "https://" + window.location.host},
  { label: "Contact", to: "#" },
];

const [emailstatus, setEmailstatus] = useState({});

function onSubmit(res) {
    setEmailstatus(res);
  }

  useEffect(() => {
    console.log("From App.js ", emailstatus);
  }, [emailstatus]);


class App extends Component {
  render() {
    return (
      <div className="box">
            <Navigation links={LINKS} />
            <Content />
            <FormEmailComponent onEmailSubmit={onSubmit} />
      </div>
    );
  }
}

export default App;
