import React from "react";

const EmailForm = () => {
    const [email, setEmail] = React.useState("");
    const [isvalid, setIsvalid] = React.useState(false);
    const [countryValid, setCountryValid] = React.useState(false);
    const [message, setMessage] = React.useState("");
    const [input, setInput] = React.useState("");


    const inputChangeHandler = (event) => {
        setInput(event.target.value);
    
    }

    const emailValidation = () => {
        const emailRegex =
          /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        return !(!input || emailRegex.test(input) === false);
      }
    
      const countryValidation = () => {
        const countryRegex = /.co$/;
        return !input || countryRegex.test(input) === false;
      }
    
      const onSubmit = () => {
        const isEmailValid = emailValidation();
        const isCountryValid = countryValidation();
    
        if (Object.keys(state.email).length === 0) {
          this.setState(
            {
              message: "Email adress is required!",
            },
            () => this.props.onEmailSubmit(this.state)
          );
        } else if (isCountryValid) {
          this.setState(
            {
              isvalid: isEmailValid,
              message: isEmailValid
                ? "Thanks for subscribing!"
                : "Email Address not valid!",
            },
            () => this.props.onEmailSubmit(this.state)
          );
        } else {
          this.setState(
            {
              countryValid: isCountryValid,
              message: isCountryValid
                ? "Thanks for subscribing!"
                : "We are not accepting subscriptions from Colombia emails!",
            },
            () => this.props.onEmailSubmit(this.state)
          );
        }
      }
  
    return (
        <div className="child-component">
            <div className="form-group mb-3">
                <label>
                    <strong>Email</strong>
                </label>
                <input
                    type="email"
                    name="email"
                    value={this.state.email}
                    onChange={inputChangeHandler}
                    className="form-control"
                />
            </div>

            <div className="d-grid">
                <button
                    type="submit"
                    className="btn btn-primary"
                    onClick={onSubmit}
                >
                    Submit
                </button>
            </div>
            <br />
            {messageTemplate}
        </div>
    );
}


export default EmailForm;
