import React from "react";

import { useEffect } from "react";

class FormEmailComponent extends React.Component {
  constructor() {
    super();
    this.state = {
      email: "",
      isvalid: false,
      countryValid: false,
      message: "",
    };
    this.onChange = this.onChange.bind(this);
  }

  onChange(e) {
    this.setState({
      email: e.target.value,
    });
  }

  emailValidation() {
    const emailRegex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    return !(!this.state.email || emailRegex.test(this.state.email) === false);
  }

  /*   countryValidation() {
    let regex = new RegExp("[a-z0-9]+@gmail.com");
    return regex.test(this.state.email);
  } */
  countryValidation() {
    const countryRegex = /.co$/;
    return !this.state.email || countryRegex.test(this.state.email) === false;
  }

    onSubmit() {
        const isEmailValid = this.emailValidation();
        const isCountryValid = this.countryValidation();


        if (Object.keys(this.state.email).length === 0) {
            this.setState(
                {
                    message: "Email adress is required!",
                },
                () => this.props.onEmailSubmit(this.state)
            );
        } else if (isCountryValid) {
            this.setState(
                {
                    isvalid: isEmailValid,
                    message: isEmailValid
                        ? "Thanks for subscribing!"
                        : "Email Address not valid!",
                },
                () => this.props.onEmailSubmit(this.state)
            );
        } else {
      this.setState(
        {
          countryValid: isCountryValid,
          message: isCountryValid
            ? "Thanks for subscribing!"
            : "We are not accepting subscriptions from Colombia emails!",
        },
        () => this.props.onEmailSubmit(this.state)
      );
    }

    // Check if email is valid
    /*     if (this.state.isvalid) {
      console.log(this.state);
    } */
  }

  render() {
    const messageTemplate = this.state.message ? (
      <div
        className={"alert alert-" + (this.state.isvalid ? "success" : "danger")}
        role="alert"
      >
        {this.state.message}
      </div>
    ) : (
      ""
    );

    return (
      <div className="child-component">
        <div className="form-group mb-3">
          <label>
            <strong>Email</strong>
          </label>
          <input
            type="email"
            name="email"
            value={this.state.email}
            onChange={this.onChange}
            className="form-control"
          />
        </div>

        <div className="d-grid">
          <button
            type="submit"
            className="btn btn-primary"
            onClick={() => this.onSubmit()}
          >
            Submit
          </button>
        </div>
        <br />
        {messageTemplate}
      </div>
    );
  }
}

export default FormEmailComponent;
